mapper.select:
SELECT p.ID, p.PERMISSION_KEY, p.PERMISSION_GROUP, p.PERMISSION_URL, p.PERMISSION_ACTION, p.PERMISSION_TYPE, p.ICON, p.SORT, p.DESCRIPTION, p.ACTIVE FROM xmo_permission p LEFT JOIN xmo_role_permission_relation rpr ON rpr.PERMISSION_ID = p.id
rpr.ROLE_ID = #{roleId}
NOT EXISTS ( SELECT PERMISSION_ID FROM xmo_role_permission_relation WHERE role_id = #{roleId} and p.id = PERMISSION_ID )

SELECT p.ID, p.PERMISSION_KEY, p.PERMISSION_GROUP, p.PERMISSION_URL, p.PERMISSION_ACTION, p.PERMISSION_TYPE, p.ICON, p.SORT, p.DESCRIPTION, p.ACTIVE FROM xmo_role_permission_relation rpr INNER JOIN xmo_permission p ON p.id = rpr.permission_id LEFT JOIN xmo_role r ON r.id = rpr.role_id LEFT JOIN xmo_user_role_relation urr ON urr.ROLE_ID = r.id LEFT JOIN xmo_user u ON urr.USER_ID = u.id
u.DISABLE = 'N' AND r.ACTIVE = 'Y' AND p.ACTIVE = 'Y'
u.id = #{userId}
